// Tests the distribution generated by Java's Math.random() function.
public  class  TestRandom {
	public static void main(String[]  args) {
		int numTimes = Integer.parseInt(args[0]);
		int bigger = 0;
		int smaller = 0;
		for (int i = 0; i < numTimes; i++) {
			double randValue = Math.random();
			if (randValue > 0.5){
				bigger++;
			} else {
				smaller++;
			};
		}
		System.out.println("> 0.5:  " + bigger + " times");
		System.out.println("<= 0.5:  " + smaller + " times");
		if (smaller != 0) {
			double ratio = (double)bigger / (double)smaller;
			System.err.println("Ratio:  " + ratio);
		}
	}
}